class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        int n=colors.size();

        vector<int> pref(n,0);

        int ans=0;

        int i=0;
        int j=1;
        int cnt=1;
        while(i<n){
             while(i<n && cnt!=k){
                 if(colors[(j-1+n)%n]!=colors[(j)%n]){
                    cnt++;
                 }
                 else{
                     i=j;
                     cnt=1;
                 }
                 j++;
             }
             if(cnt==k){
                //cout<<i<<endl;
                ans++;
                cnt--;
             }
             i++;
        }

        return ans;
    }
};
