class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int cs[n * 2], fs[2 * n];
        for(int i = 0; i < n; ++i)
            cs[i] = cs[i + n] = colors[i];
        fs[0] = 1;
        for(int i = 1; i < 2 * n; ++i){
            if(cs[i] == cs[i - 1])
                fs[i] = 1;
            else
                fs[i] = fs[i - 1] + 1;
        }
        int res = 0;
        for(int i = n; i < 2 * n; ++i){
            if(fs[i] >= k) ++res;
        }
        return res;
    }
};
