class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n =  arr.size();
        int res = 0, count = 1;
        vector<int> extended(2 * n);
        for (int i = 0; i < 2 * n; ++i)
            extended[i] =  arr[i % n];

        for (int i = 1; i < n + k - 1; ++i) {
            if (extended[i] != extended[i - 1]) {
                count++;
                if (count >= k)
                    res++;
            } else 
                count = 1;

        }   
        return res;
    }
};
