class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int cnt=1;
        int ans=0;
        for(int i=1;i<n+k-1;i++){
            if(colors[i%n]!=colors[(i-1)%n]){
                cnt++;
            }
            else{
               if(cnt>=k){ ans+=cnt-k+1; }
               cnt=1;
            }
        }
        if(cnt>=k) ans+=cnt-k+1;
        return ans;
    }
};
