func numberOfAlternatingGroups(colors []int, k int) int {
    n, res := len(colors), 0
    last, cur := -1, 0
    for i := 0; i < n + k - 1; i++ {
        if last == colors[i % n] {
            cur = 0
        }
        cur++
        last = colors[i % n]
        if cur >= k {
            res++
        }
    }
    return res
}
