class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int count = 0;
        int n = colors.length;
        int pre = 0;
        for(int i = 1; i < n + k; i++){
            if(colors[i % n] != colors[(i - 1 + n) % n]){
                pre++;
                if(pre >= k){
                    count++;
                }
            }else {
                pre = 1;
            }
        }
        return count;
    }
}
