class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        las = colors[0]
        sm = 1
        res = 0

        for i in range(1,n+k-1):
            if colors[i%n] != las:
                sm += 1
                if sm == k:
                    res += 1
                    sm -= 1
            else:
                sm = 1
            las = colors[i%n]
        return res
