class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        long i=0,j=0;
        int count=0;
        long n = colors.length;
        long maxIdx = n + k;

        while(j<maxIdx){
            j++;
            int currIdx = (int)(j%n);
            int prevIdx = (int)((j-1)%n);
            if(colors[currIdx]==colors[prevIdx]){
                i=j;
                continue;
            }
            if(j-i+1==k){
                count++;
                i++;
            }
            if(i>=n) break;
        }
        return count;
    }
}
