class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> v=colors;
        vector<int> ans;
        int d=1;
        for(int i=1; i<v.size(); i++){
            if(v[i]!=v[i-1]){
                d++;
            }
            else{
                ans.push_back(d);
                d=1;
            }
        }
        ans.push_back(d);
        if(v[0]!=v[v.size()-1]){
            // cout<<1;
            if(ans.size()==1){
                return v.size();
            }
            ans[0]+=ans[ans.size()-1];
            int u=0;
            for(int i=0; i<ans.size()-1; i++){
                u+=max(0,ans[i]-k+1);
            }
            return u;
        }
        else{
            int u=0;
            for(int i=0; i<ans.size(); i++){
                u+=max(0,ans[i]-k+1);
            }
            return u;
        }
    }
};
