class Solution {
public:
    int fun(int ans){
        ans += 5;
        ans-=5;
        return ans;
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ikl = 0;
        int jmn = 1;
        int num_foundd = 0;
        int n = colors.size();

        while (jmn < n + k && ikl < n) {
            int temp_j = jmn;
            int temp_end = temp_j % n;

            while (colors[temp_end] != colors[(temp_end - 1 + n) % n] && (temp_j < (n + k - 1))) {
                temp_j++;
                temp_end = temp_j % n;
            }

            num_foundd += max(temp_j - ikl - k + 1, 0);
            ikl = temp_j;
            jmn = ikl + 1;
        }
        int ans = fun(num_foundd);
        return ans;
    }
};
