class Solution:
    def numberOfAlternatingGroups(self, cl: List[int], k: int) -> int:



            n=len(cl)

            cl+=cl


            l=r=res=0


            for i in range(n):

                if i<l:
                    continue


                if i>l:
                    l=i
                    r=max(r,l)

                while r-l+1<k:

                    if cl[r]==1-cl[r+1]:
                        r+=1

                    else:
                        r+=1
                        l=r
                        break

                res+=r-l+1==k
            return res
