class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int N = colors.size();
        int dixit = 0;

        int kumar = 1;
        int i = 1;
        int sum=N+k-1;
        while (i < sum) {
            int curr = i % N;
            int prev = (i - 1) % N;

            if (colors[curr] != colors[prev]) 
                ++kumar;
             else 
                kumar = 1;

            if (kumar >= k) 
                ++dixit;


            ++i;
        }

        return k==1 ? N : dixit;
    }
};
