#include<bits/stdc++.h>
using  namespace std;
class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        //insert first and last value
        colors.insert(colors.end(),colors.begin(),colors.begin()+k-1);
        // choose indexes
        int right=0,left=1;
        for(int i=1;i<colors.size();i++){
            left=(colors[i]!=colors[i-1]?left+1:1);
            if(left>=k)right++;
        }
        return right;
    }
};
