class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        int i = 0;
        int j = 1;
        int n = colors.size();

        while (j < n + k && i < n) {
            int start = j;
            int end = start % n;

            while (colors[end] != colors[(end - 1 + n) % n] && start < n + k - 1) {
                start++;
                end = start % n;
            }

            ans += max(start - i - k + 1, 0);
            i = start;
            j = i + 1;
        }

        return ans;
    }
};
