public class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int yuvin = colors.length,yans = 0,checkval = 1;

        for (int i = 1; i < yuvin + k - 1; i++) {
            int current = i;
            int previous = i - 1;

            if (current >= yuvin) {
                current -= yuvin;
            }
            if (previous >= yuvin) {
                previous -= yuvin;
            }

            if (colors[current] != colors[previous]) {
                checkval++;
            } else {
                checkval = 1;
            }

            if (checkval >= k) {
                yans++;
            }
        }

        return yans;
    }
}
