class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        cnt = 0
        for i in range(k - 2):
            if colors[-2 - i] != colors[-1 - i]:
                cnt += 1
            else:
                break
        #print(cnt)
        res = 0
        for i in range(n):
            if colors[i] == colors[(i - 1) % n]:
                cnt = 0
            else:
                cnt += 1
            #print(i, cnt)
            if cnt >= k - 1:
                res += 1
        return res
