class Solution:
    def numberOfAlternatingGroups(self, color_132: List[int], leaf_132: int) -> int:
        fnds_132 = []
        fnsh_132 = color_132[0]
        current = 1
        same = False

        for i in range(1, len(color_132)):
            if color_132[i] == fnsh_132:
                fnds_132.append(current)
                current = 1
                same = True
            else:
                current += 1
            fnsh_132 = color_132[i]

        fnds_132.append(current)

        if len(fnds_132) == 1 and fnds_132[0] == len(color_132) and color_132[0] != color_132[-1] and not same:
            return len(color_132)

        if color_132[0] != color_132[-1] and len(fnds_132) > 1:
            fnsh_132_group = fnds_132.pop()
            fnds_132[0] += fnsh_132_group

        result = 0
        for group in fnds_132:
            result += max(0, group - (leaf_132 - 1))

        return result
