class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int red = 1, blue = 1;
        int res = 0, n = colors.size();
        int last = -1;
        for(int i = 1; i < 2 * n; i++)
        {
            int temp = red;
            red = colors[i % n] == 0 && colors[(i - 1) % n] == 1? blue + 1 : 1;
            blue = colors[i % n] == 1 && colors[(i - 1) % n] == 0 ? temp + 1 : 1;
            if(i % n == last) break;
            if(max(red, blue) >= k) 
            {
                if(last == -1) last =i; 
                res++;
            }
        }
        return res;
    }
};
