class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size();
        vector<int> pt(n);
        int start=n;
        for(int i=0;i<n-1;i++){
            if(c[i]==c[i+1]) pt[i]=1;
            if(pt[i]==1) start=min(start, i);
        }
        if(start==n && n%2==0) return n;
        else if(start==n) return n-k+1;
        int p=start;
        int ans=0;
        for(int i=start+1;i<n;i++){
            if(pt[i]==1){
                if(i-p>=k){
                  ans+=(i-p-k+1);  
                }
                p=i;
            }
        }
        if(c[0]!=c[n-1]){
            int tot=start+1 + n-1-p;
            if(tot>=k){
                ans+=(tot-k+1);
            }
        }
        else {
            if(start+1>=k) {
                ans+=(start+1-k+1);
            }
            if(n-1-p>=k){
                ans+=(n-1-p-k+1);
            }
        }

        return ans;
    }
};
