class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int found = 0;
        int i = 0;
        int j = 1;

        while (j < n + k && i < n) {
            int tempj = j;
            int end = tempj % n;
            while (colors[end] != colors[(end - 1 + n) % n] && tempj < (n + k - 1)) {
                tempj += 1;
                end = tempj % n;
            }

            found += max(tempj - i - k + 1, 0);
            i = tempj;
            j = i + 1;
        }

        return found;
    }
};
