class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(); 

        int ans = 0, temp = 1; 

        for(int i=1; i<n+k-1; i++){
            if(colors[i%n] != colors[(i-1)%n]){
                if(temp < k) temp++;
                if(temp >= k){
                    ans++; 
                }
            }
            else{
                temp = 1; 
            }
        }

        return ans; 
    }
};
