class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        // [0,1,0,1,0 || 0,1,0,1,0], k = 3
        int ans=0;
        int n=c.size();
        int l=0,r=1;
        vector<int> colors(c.begin(),c.end());
        for(int i:c){
            colors.push_back(i);
        }
        while(r<n+k-1){
            //cout<<"L "<<l<<"R "<<r<<"  ";
            while(r<n+k-1 && r-l<k){
               // cout<<colors[r-1]<<"  "<<colors[r]<<"  ";
                if(colors[r]!=colors[r-1]){
                    r++;
                }else{
                    break;
                }
            }
            if(r-l==k){
                ans++;
                l++;
            }else{
                l=r;
                r++;
            }
            //cout<<endl;
        }

        return ans;

    }
};
