class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int N = colors.length;
        int cnt = 0;
        if (k == 1) {
            return N;
        }
        int len = 1,i = 1;
        while (i < N + k - 1) {
            int cur = i;
            int prev = i - 1;
            if (cur >= N) {
                cur -= N;
            }
            if (prev >= N) {
                prev -= N;
            }
            if (colors[cur] != colors[prev]) {
                ++len;
            } else {
                len = 1;
            }
            if (len >= k) {
                ++cnt;
            }
            ++i;
        }
        return cnt;
    }
}
