class Solution {
public:
    int numberOfAlternatingGroups(vector<int> &v, int k) {

        int n = v.size();
        vector<int> temp(n,1);

        for(int i=0;i<n;i++){
            if(v[i]==v[(i-1+n)%n] || v[i]==v[(i+1)%n]){
                temp[i] = 0;
            }
        }

        // for(auto it : temp){
        //     cout<<it<<" ";
        // }
        // cout<<endl;

        int sum = 0;
        for(int i=0;i<k-2;i++){
            sum += temp[i];
        }

        int l = 0;
        int r = k-2;
        int cnt = 0;

        if(sum==k-2){
            cnt++;
        }

        while(l<n-1){
            sum += temp[r];
            sum -= temp[l];
            // cout<<sum<<endl;
            if(sum==k-2){
                cnt++;
            }
            r = (r+1)%n;
            l++;
        }

        return cnt;

    }
};
