class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int count=0;
        int alt_start=1;
        vector<int>alt_upto(n,1);
        for(int i=1;i<n;i++){
            if(colors[i]!=colors[i-1]){
                alt_upto[i]=alt_upto[i-1]+1;
                if(alt_upto[i]==i+1) alt_start=alt_upto[i];
                if(alt_upto[i]>=k) count++;
            }
        }
        if(colors[0]!=colors.back()){
            alt_start=min(alt_start,k-1);
            alt_upto[n-1]=min(alt_upto[n-1],k-1);
            int s=alt_upto[n-1]+alt_start;
            if(s>=k) count+=s-k+1;
        }
        return count;
    }
};
