class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int j = 0, n = k - 1;
        while(j < n)
            colors.push_back(colors[j++]);

        int count = 1, res = 0, maxm = 0;
        n = colors.size();
        for(int i = 1; i < n; i++){
            if(colors[i] != colors[i - 1])
                count++;
            else{
                if(count >= k)
                    res += count - k + 1;
                count = 1;
            }
        }
        if(count >= k)
            res += count - k + 1;
        return res;
    }
};
