class Solution {
public:
    int n;

    int f(vector<int>& co, int k) {
        int le = 0, re = 1;
        for (int i = 1; i < n + k - 1; ++i) {
            if (co[i] != co[i - 1]) {
                re++;
            } else {
                re = 1;
            }

            if (re >= k) {
                le++;
            }
        }
        return le;
    }

    int numberOfAlternatingGroups(vector<int>& co, int k) {
        n = co.size();
        for (int i = 0; i < k - 1; ++i) {
            co.push_back(co[i]);
        }
        int le = f(co, k);
        return le;
    }
};
