class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();

        for(int i = 0; i < n; i++){
			a.push_back(a[i]);
		}

		int m = n;

		n = a.size();

		vector<int> r(n);

		for(int i = n-1; i >= 0; i--){
			if(i == n-1 || a[i] == a[i+1]){
				r[i] = i;
			}
			else{
				r[i] = r[i+1];
			}
		}

		int ans = 0;
		for(int i = 1; i <= m; i++){
			int d = r[i] - i + 1;
			if(d >= k){
				ans++;
			}
		}

		return ans;

    }
};
