class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();
         int ans = 0;
        int sz=n;
        vector <int> dp (2*n);
        for (int i = 0; i < n; i++) {
            c.push_back(c[i]);
        }
        n = c.size();

        for (int i = n - 1; i >= 0; i--) {
            dp[i] = i;
            if (i + 1 < n && c[i + 1] != c[i]) dp[i] = dp[i + 1];
        }

        for (int i = 0; i <sz; i++) {
            if (dp[i] - i + 1 >= k) ans++;
        }
        return ans;
    }
};
