class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& A, int k) {
        fun1(A, k);
        return fun2(A, k);
    }

public:
    void fun1(vector<int>& A, int k) {
        A.insert(A.end(), A.begin(), A.begin() + k - 1);
    }

    int fun2(vector<int>& A, int k) {
        int R = 0, N = 1;
        size_t i = 1;
        while (i < A.size()) {
            N = fun3(A, i, N);
            if (N >= k) 
                ++R;
            ++i;
        }
        return R;
    }

    int fun3(vector<int>& A, size_t i, int N) {
        return (A[i] != A[i - 1]) ? N + 1 : 1;
    }
};
