class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

         int cnt = 0;
        int m = 1;
        for(int i=0;i<k-1;i++){
            colors.push_back(colors[i]);
        }
        int n = colors.size();
        for(int i=1;i<n;i++){
            if(colors[i] != colors[i-1]){
                m++;
            }

            if(colors[i] == colors[i-1]){
                m = 1;
            }
            if(m>=k)cnt++; 
        }
        return cnt;
    }

};
