class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size(), ans = 0;
        vector<int> v(c.begin(), c.end());
        for(auto it: c) v.push_back(it);

        int j = 0, prev = c[0];

        for(int i = 1; i < n + k - 1; i++){
            if(v[i] == v[i-1]) j = i;
            if(i - j + 1 == k){
                ans ++;
                j++;
            }

            prev = c[i];
        }

        return ans;
    }
};
