class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {
        vector<int>temp(nums.begin(),nums.end());

        for(int i=0;i<k-1;i++){
            temp.push_back(nums[i]);
        }

        int n=temp.size();
        int cnt=1;
        int ans=0;
        for(int i=1;i<n;i++)
        {
            if(temp[i-1]!=temp[i]){
                cnt++;
            }
            else{
                if(cnt >= k){
                    ans += (cnt-k+1);
                }
                cnt = 1;
            }
        }
        if(cnt >= k){
            ans += (cnt-k+1);
        }
        return ans;
    }
};
