class Solution {
    public int numberOfAlternatingGroups(int[] c, int k) {
        int n = c.length;
        int[] newCol = new int[n + k - 1];

        // Copy the original array into the new array
        for (int i = 0; i < n; ++i) {
            newCol[i] = c[i];
        }

        // Extend the array by appending the first (k - 1) elements of the original array
        for (int i = 0; i < k - 1; ++i) {
            newCol[n + i] = c[i];
        }

        int res = 0;
        int N = 1;

        // Iterate through the new array and count alternating groups
        for (int i = 1; i < newCol.length; ++i) {
            if (newCol[i] != newCol[i - 1]) {
                ++N;
            } else {
                N = 1;
            }
            if (N >= k) {
                ++res;
            }
        }

        return res;
    }
}
