class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int arr[] = new int[colors.length + k - 1];
        int n = colors.length;

        for(int i = 0; i < colors.length; i++) {
            arr[i] = colors[i];
        }

        for(int i = 0; i < k - 1; i++) {
            arr[i + n] = colors[i];
        }

        int prev = arr[0] == 0 ? 1 : 0;
        int ans = 0;

        int i = 0;
        int j = 0;

        while(j < arr.length) {
            if(arr[j] == prev)
                i = j;

            while(i < j && (j - i + 1) > k) {
                i++;
            }

            if(j - i + 1 == k)
                ans++;
            prev = arr[j];
            j++;
        }

        return ans;
    }
}
