class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int groupSize) {
        int totalTiles = colors.size();
        int alternatingGroupCount = 0;

        if (groupSize == 1) {
            return totalTiles;
        }

        int currentAlternatingLength = 1;
        int index = 1;

        while (index < totalTiles + groupSize - 1) {
            int currentIndex = index;
            int previousIndex = index - 1;

            if (currentIndex >= totalTiles) {
                currentIndex -= totalTiles;
            }
            if (previousIndex >= totalTiles) {
                previousIndex -= totalTiles;
            }

            if (colors[currentIndex] != colors[previousIndex]) {
                ++currentAlternatingLength;
            } else {
                currentAlternatingLength = 1;
            }

            if (currentAlternatingLength >= groupSize) {
                ++alternatingGroupCount;
            }

            ++index;
        }

        return alternatingGroupCount;
    }
};
