#include <vector>
using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int count = 0;
        bool isAlternating = true;

        for (int j = 0; j < k - 1; ++j) {
            if (colors[j] == colors[j + 1]) {
                isAlternating = false;
                break;
            }
        }
        count += isAlternating;

        for (int i = 1; i < n; ++i) {
            if (isAlternating) {
                isAlternating = (colors[(i + k - 2) % n] != colors[(i + k - 1) % n]);
            } else {
                isAlternating = true;
                for (int j = 0; j < k - 1; ++j) {
                    if (colors[(i + j) % n] == colors[(i + j + 1) % n]) {
                        isAlternating = false;
                        break;
                    }
                }
            }
            count += isAlternating;
        }

        return count;
    }
};
