class Solution:
    def numberOfAlternatingGroups(self, a: List[int], k: int) -> int:
        a = a*2
        n = len(a)
        z = 0
        c = [0]*(n+1)
        for i in range(n-1):
            if a[i] == a[(i+1)]:
                c[i+1] = 1
        for i in range(n):
            c[i+1] += c[i]
        for i in range(n//2):
            z += (c[(i+k-1)]-c[i]==0)
        return z
