class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        for(int i=0;i<k;i++) colors.push_back(colors[i]);
        int n=colors.size();
        vector<int> alter(n+1,0);
        int ans=0;

        for(int i=1;i<n;i++){
            alter[i]+=alter[i-1];
            if(colors[i]!=colors[i-1]) alter[i]++;
        }
        for(int i=k;i<n;i++){
            if(alter[i]==alter[i-k+1]+(k-1)) ans++;
        }
        return ans;

    }
};
