from collections import deque
from typing import List

class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        if n == 0:
            return 0

        count = 0
        extended_colors = colors + colors[:k - 1]
        q = deque([extended_colors[0]])
        prev = extended_colors[0]

        for i in range(1, len(extended_colors)):
            if prev != extended_colors[i]:
                q.append(extended_colors[i])
                prev = extended_colors[i]
                if len(q) == k:
                    count += 1
                    q.popleft()
            else:
                q.clear()
                q.append(extended_colors[i])
                prev = extended_colors[i]

        return count
