    int numberOfAlternatingGroups(vector<int>& col, int k) {
        int n = col.size(),i=0,j=1,cnt=0;        
        while (j < n + k && i < n) {
            int a=j,b=a%n;
            while (col[b] != col[(b-1+n)%n] && a<n+k-1) {
                a++;
                b=a%n;
            }
            cnt+=max(a-i-k+1, 0);
            i=a;
            j=i+1;
        }        
        return cnt;
    }
};
