class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int num_found = 0;
        int i = 0;
        int j = 1;

        while (j < n + k && i < n) {
            int temp_j = j;
            int temp_end = temp_j % n;
            while (colors[temp_end] != colors[(temp_end - 1 + n) % n] && temp_j < (n + k - 1)) {
                temp_j += 1;
                temp_end = temp_j % n;
            }

            num_found += max(temp_j - i - k + 1, 0);
            i = temp_j;
            j = i + 1;
        }

        return num_found;

    }
};
