class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int cnt=0;
        int i=0;
        int j=1;
        while(j<n){
            if(colors[j]!=colors[j-1] and (j-i+1)==k){
               cnt++;
               i++;
            }
            else if(colors[j]==colors[j-1]){
                i=j;
            }
            j++;
        }
        j=0;
        if(colors[j]==colors[n-1])return cnt;
        if((n-i)+(j+1)==k){
            cnt++;
            i++;
        }
        j=1;
        while(j<k-1){
            if(colors[j]!=colors[j-1] and ((n-i)+(j+1)==k)){
               cnt++;
               i++;
            }
            else if(colors[j]==colors[j-1]){
                return cnt;
            }
            j++;
        }
        return cnt;
    }
};
