class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        colors = colors + colors[:k - 1]
        n = len(colors)
        cnt = [0] * n
        for i in range(n):
            if i == 0:
                cnt[i] = 1
            else:
                if colors[i] != colors[i - 1]:
                    cnt[i] = cnt[i - 1] + 1
                else:
                    cnt[i] = 1
        return sum(cnt[i] >= k for i in range(n))
