class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        int n=colors.size();
        vector<int>a;
        for(int i=0;i<n;i++)a.push_back(colors[i]);
        for(int i=0;i<n;i++)a.push_back(colors[i]);


        int ans=0;
        vector<int>dp(2*n+1,1);

        for(int i=1;i<2*n;i++){
            if(a[i]!=a[i-1]){
                dp[i]=dp[i-1]+1;
            }
            else if(i>=n){
                break;
            }

            if(i-k+1>=n)break;
            if(i>=(k-1)){
               if((dp[i]-dp[i-k+1]+1)==k)ans++;
            }
        }

        // cout<<a.size()<<endl;
        // for(int i=0;i<2*n;i++)cout<<dp[i]<<" "; cout<<endl;
        return ans;
    }
};
