class Solution {
public: 
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int N = colors.size();
        int count = 0;

        if (k == 1) {
            return N;
        }

        int altLength = 1;
        for (int i = 1; i < N + k - 1; ++i) {
            int currentIdx = i % N;
            int prevIdx = (i - 1) % N;

            if (colors[currentIdx] != colors[prevIdx]) {
                altLength++;
            } else {
                altLength = 1;
            }

            if (altLength >= k) {
                count++;
            }
        }

        return count;
    }
};
