class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int ans = 0;
        int i = 0;
        int j = 1; int n = arr.size();
        while(j < k + n) {
            if(i >= n) break;
            int j1 = j;
            int tmp = j1 % n;
            while(arr[tmp] != arr[(tmp - 1 + n) % n] && j1 < (n + k - 1)) {
                j1++;
                tmp = j1 % n;
            } 
            ans = ans + max(j1 - i - k + 1, 0);
            i = j1; j = i + 1;
        }

        return ans;
    }
};
