class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> ans;
        for(int i=0;i<colors.size();i++){
            ans.push_back(colors[i]);
        }

        for(int i=0;i<k-1;i++){
            ans.push_back(colors[i]);
        }

        int n=ans.size();

        int sum=0;

        for(int i=0;i<ans.size();i++){
            cout<<ans[i]<<" ";
        }
        cout<<endl;

        int count=1;
        int num=ans[0];
        for(int i=1;i<n;i++){
            if(num!=ans[i]){
                count++;
                num=ans[i];
            }
            else{
                if(count-k+1>=0){
                    sum+=count-k+1;
                }
                // sum+=(count-k+1);
                num=ans[i];
                count=1;
            }
        }

        if(count-k+1>=0){
            sum+=count-k+1;
        }

        return sum;
    }
};
