class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {
        int n=nums.size(),cnt=1,ans=0;
        for(int i=1;i<n+k-1;i++)
        {
            if(nums[i%n]!=nums[(i-1)%n])cnt++;
            else
            {
                cnt=1;
            }
            if(cnt==k)
            {
                ans++;
                cnt--;
            }
        }
        return ans;
    }
};
