class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int groupCount = 0;

        if (k == 1) {
            return n;
        }

        int currentAlternatingLength = 1;
        int index = 1;
        while (index < n + k - 1) {
            int currentIndex = index;
            int previousIndex = index - 1;

            if (currentIndex >= n) {
                currentIndex -= n;
            }
            if (previousIndex >= n) {
                previousIndex -= n;
            }

            if (colors[currentIndex] != colors[previousIndex]) {
                ++currentAlternatingLength;
            } else {
                currentAlternatingLength = 1;
            }

            if (currentAlternatingLength >= k) {
                ++groupCount;
            }

            ++index;
        }

        return groupCount;
    }
};
